import React, { useState } from "react";
import {
  Card,
  CardContent,
  CardMedia,
  Typography,
  Button,
  Grid,
  IconButton,
} from "@mui/material";
import { Favorite, Delete } from "@mui/icons-material";
import { motion } from "framer-motion";
import {  useNavigate } from "react-router-dom";

const initialWishlist = [
  {
    id: 1,
    name: "Modern Luxury Sofa",
    image: "data:image/jpeg;base64,",
    price: "$450",
  },
  {
    id: 2,
    name: "Antique Wall Clock",
    image: "data:image/jpeg;base64,",
    price: "$120",
  },
  {
    id: 3,
    name: "Elegant Floor Lamp",
    image: "data:image/jpeg;base64,",
    price: "$75",
  },
];

const Wishlist = () => {
  const [wishlist, setWishlist] = useState(initialWishlist);

  const navigate = useNavigate();

  const handleRemove = (id) => {
    setWishlist(wishlist.filter((item) => item.id !== id));
  };

  const handleNavigation = (path) => {
    navigate(path);
  };

  return (
    <div className="sm:px-24 px-8 py-20">
      <Typography variant="h4" gutterBottom>
        Your Wishlist ❤️
      </Typography>

      <Grid container spacing={3}>
        {wishlist.map((item) => (
          <Grid item xs={12} sm={3} key={item.id}>
            <motion.div whileHover={{ scale: 1.05 }}>
              <Card className="shadow-lg">
                <CardMedia component="img" height="100" width={"100"} image={item.image} />
                <CardContent>
                  <Typography variant="h6">{item.name}</Typography>
                  <Typography variant="body2" color="text.secondary">
                    {item.price}
                  </Typography>
                  <div className="flex justify-between mt-4">
                    <Button variant="contained" color="primary" onClick={() => handleNavigation("/cart")}>
                      Move to Cart
                    </Button>
                    <IconButton onClick={() => handleRemove(item.id)} color="error">
                      <Delete />
                    </IconButton>
                  </div>
                </CardContent>
              </Card>
            </motion.div>
          </Grid>
        ))}
      </Grid>

      {wishlist.length === 0 && (
        <Typography variant="h6" color="text.secondary" className="mt-4">
          No items in your wishlist.
        </Typography>
      )}
    </div>
  );
};

export default Wishlist;
